delimiter ;
CREATE VIEW `view_actual_eventlist` AS select `events_event`.`eventid` AS `eventid`,`events_event`.`name` AS `name`,`events_event`.`genre` AS `genre`,`events_event`.`description_md` AS `description_md`,`events_event`.`description_html` AS `description_html`,date_format(`events_event`.`start`,'%b','de_DE') AS `monat`,date_format(`events_event`.`start`,'%M','de_DE') AS `monat_lang`,date_format(`events_event`.`start`,'%d','de_DE') AS `tag`,date_format(`events_event`.`start`,'%a','de_DE') AS `wtag`,date_format(`events_event`.`start`,'%H:%i','de_DE') AS `zeit`,date_format(`events_event`.`start`,'%W','de_DE') AS `wtag_lang`,date_format(`events_event`.`start`,'%Y','de_DE') AS `jahr`,date_format(`events_event`.`start`,'%d.%m.%Y','de_DE') AS `datum_de`,`events_event`.`start` AS `start`,`events_event`.`stop` AS `stop`,`events_event`.`external_url` AS `external_url`,ifnull(`events_event`.`imagebild`,532) AS `imagebild`,ifnull(`events_bilder`.`id`,532) AS `teaser_img_id` from (`events_event` left join `events_bilder` on(`events_bilder`.`eventid` = `events_event`.`eventid` and `events_bilder`.`typ` = 'teaser')) where `events_event`.`start` > current_timestamp() or `events_event`.`stop` > current_timestamp() order by `events_event`.`start`;
